def add_data(apps, schema_editor):
    Section = apps.get_model("reports", "Section")
    Quote = apps.get_model("reports", "Quote")

    print("Loading Sections!")
    section_id_mapping = {}
    for section_name in SECTIONS:
        s = Section(name=section_name)
        s.save()
        section_id_mapping[section_name] = s.id

    print("Produced section mapping:")
    print(section_id_mapping)

    print("Loading Quotes!")
    for quote in QUOTES:
        section_id = section_id_mapping[quote["section"]]
        q = Quote(
            content=quote["content"],
            section_id=section_id,
            is_positive=quote["is_positive"],
        )
        q.save()


